mapper.sql:
FROM board a
JOIN infrMember b on a.infrMmSeq = b.infrMmSeq
LEFT JOIN boardUploaded c on a.bdSeq = c.pSeq
WHERE 1=1
	AND a.delNy = #{shDelNy}

		AND a.regDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
		AND a.modDateTime BETWEEN #{shDateStart} AND #{shDateEnd}


		AND b.infrMmNickname LIKE CONCAT('%',#{shValue},'%')
		AND a.bdTitle LIKE CONCAT('%',#{shValue},'%')
		AND a.bdDiv LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
INSERT INTO board (
	infrMmSeq
	,bdDiv
	,bdTitle
	,bdContent
	,regDatetime
	,delNy
	,viewCount
)
VALUES (
	#{infrMmSeq}
	,#{bdDiv}
	,#{bdTitle}
	,#{bdContent}
	,now()
	,1
	,0
)

		SELECT last_insert_id()

mapper.update:
UPDATE board
SET
	bdDiv = #{bdDiv}
	,bdTitle = #{bdTitle}
	,bdContent = #{bdContent}
	,delNy = #{delNy}
WHERE 1=1
	AND bdSeq = #{bdSeq}

mapper.delete:
DELETE FROM board 
WHERE 1=1
	AND bdSeq = #{bdSeq}

mapper.select:

SELECT 
	infrMmSeq
FROM bdLike 
WHERE 1=1
	AND bdSeq = #{bdSeq}
	AND infrMmSeq = #{infrMmSeq}

mapper.insert:
INSERT INTO bdLike (
	bdSeq
	,infrMmSeq
)
VALUES (
	#{bdSeq}
	,#{infrMmSeq}
)

	SELECT last_insert_id()

mapper.delete:
DELETE FROM bdLike
WHERE 1=1
	AND bdSeq = #{bdSeq}
	AND infrMmSeq = #{infrMmSeq}

mapper.insert:
INSERT INTO bdComment (
	content
	,regDatetime
	,infrMmSeq
	,board_seq
	,delNy
)
VALUES (
	#{content}
	,now()
	,#{infrMmSeq}
	,#{bdSeq}
	,1
)

	SELECT last_insert_id()

mapper.delete:
DELETE FROM bdComment
WHERE 1=1
	AND cmSeq = #{cmSeq}
	AND infrMmSeq = #{infrMmSeq}

mapper.insert:
INSERT INTO bdReComment (
	reContent
    ,regDatetime
    ,comment_seq
    ,infrMmSeq
)
VALUES(
	#{reContent}
    ,now()
    ,#{cmSeq}
    ,#{infrMmSeq}
)

	SELECT last_insert_id()

mapper.select:
SELECT COUNT(*)FROM bdReComment
	WHERE 1=1
	AND comment_seq = #{cmSeq}
