mapper.sql:
FROM infrCode a
WHERE 1=1
	AND infrCcDelNy = #{shDelNy}

		AND infrCcSeq = #{shValue}
		AND infrCcNameKor LIKE CONCAT('%',#{shValue},'%')
		AND infrCcNameEng LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
    	INSERT INTO infrCode(
    		infrCcNameKor
    		,infrCcNameEng
    		,infrCcUseNy
    		,infrCcDelNy
    		,regDatetime
    		,infrCodeGroupSeq
    	)VALUES(
    		#{infrCcNameKor}
    		,#{infrCcNameEng}
    		,#{infrCcUseNy}
    		,#{infrCcDelNy}
    		,now()
    		,#{infrCodeGroupSeq}
    	)

			SELECT last_insert_id()
