mapper.sql:
FROM infrMember a
WHERE 1=1
AND infrMmDelNy = #{shDelNy}

	AND infrMmSeq = #{shValue}
	AND infrMmName LIKE CONCAT('%',#{shValue},'%')
	AND infrMmId LIKE CONCAT('%',#{shValue},'%')

mapper.select:
select * from infrMember
WHERE 1=1 
AND infrMmSeq = #{mmSsSeq}

select count(*)


	from infrMember 
	where 
	infrMmId = #{shDupleStr}


	from infrMember 
	where 
	infrMmName = #{shDupleStr}


	from infrMember 
	where 
	infrMmNickname = #{shDupleStr}


	from infrMember 
	where 
	infrMmPhone = #{shDupleStr}

mapper.update:
UPDATE infrMember
SET
	infrMmId = #{infrMmId}
	,infrMmName = #{infrMmName}
	,infrMmPw = #{infrMmPw}
	,infrMmEmailId = #{infrMmEmailId}
	,infrMmNickname = #{infrMmNickname}
	,infrMmGender = #{infrMmGender}
	,infrMmBod = #{infrMmBod}
	,infrMmPhone = #{infrMmPhone}
	,infrMmDelNy = #{infrMmDelNy}
	,infrMmUseNy = #{infrMmUseNy}
WHERE 1=1
	AND infrMmSeq = #{mmSsSeq}
