mapper.update:
update sgWorld set onLiveNy = #{onLiveNy}
where 1=1
and sgwLink = #{sgwLink}

mapper.select:
select
	count(*)
from sgWorldRoom
where 1=1
and sgwSeq =#{sgwSeq}
and infrMmSeq = #{infrMmSeq}

mapper.insert:
insert into sgWorldRoom(
	infrMmSeq,
	sgwSeq,
	avatarSeq
)values(
	#{infrMmSeq},
	#{sgwSeq},
	#{avatarSeq}
)

mapper.delete:
delete from sgWorldRoom where 1=1
and infrMmSeq = #{infrMmSeq}
and sgwSeq = #{sgwSeq}
